package class08NestedForLoopBreakContin;

public class AselPracticeForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int a=0; a<=4; a++) {
			char sign='*';
			for (int b=1; b<=a+1; b++) {
				System.out.print(sign);
			} System.out.println();
		}
	}

}
